mapper.select:
SELECT 

FROM t_wecha_new_reply a

WHERE a.id = #{id}

SELECT 

FROM t_wecha_new_reply a




		AND a.id = #{id}


		AND a.un_open = #{unOpen}


		AND a.reply_type = #{replyType}


		AND a.content_type = #{contentType}


		AND a.key_type = #{keyType}


		AND a.keywords = #{keywords}


		AND a.reply_way = #{replyWay}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.deleted = #{deleted}


		AND a.wechat_id = #{wechatId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_wecha_new_reply SET
deleted = 1
WHERE id = #{id}
