mapper.select:
SELECT

FROM t_wx_reply_rule_content a


    a.deleted = 0

        AND a.id = #{id}


        AND a.wechat_id = #{wechatId}


        AND a.rule_id = #{ruleId}

SELECT

FROM t_wx_reply_rule_content a


    a.deleted = 0

        AND a.id = #{id}


        AND a.wechat_id = #{wechatId}


        AND a.rule_id = #{ruleId}


        AND a.content_type = #{contentType}


        AND a.content = #{content}


        AND a.media_id = #{mediaId}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

